import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useState } from "react";
import Navbar from "./components/Navbar";
import AllCampaigns from "./components/AllCampaigns";
import CreateCampaign from "./components/CreateCampaign";
import MyCampaigns from "./components/MyCampaigns";
import Dashboard from "./components/Dashboard";

function App() {
  const [walletAddress, setWalletAddress] = useState("");

  // ðŸ”¥ Global Toast State
  const [toast, setToast] = useState({
    show: false,
    message: "",
    type: "success",
  });

  const showToast = (message, type = "success") => {
    setToast({ show: true, message, type });

    setTimeout(() => {
      setToast({ show: false, message: "", type: "success" });
    }, 3500);
  };

  return (
    <Router>
      <Navbar walletAddress={walletAddress} setWalletAddress={setWalletAddress} />

      {/* ðŸ”¥ MODERN TOAST */}
      {toast.show && (
        <div style={toastStyle}>
          <div
            style={{
              ...iconStyle,
              background: toast.type === "error" ? "#ef4444" : "#22c55e",
            }}
          >
            {toast.type === "error" ? "âœ•" : "âœ“"}
          </div>
          <span>{toast.message}</span>
        </div>
      )}

      <Routes>
        <Route path="/" element={<AllCampaigns />} />
        <Route path="/create" element={<CreateCampaign showToast={showToast} />} />
        <Route path="/mycampaigns" element={<MyCampaigns />} />
        <Route
          path="/campaign/:id"
          element={<Dashboard showToast={showToast} />}
        />
      </Routes>
    </Router>
  );
}

export default App;

/* ================= STYLES ================= */

const toastStyle = {
  position: "fixed",
  top: "20px",
  left: "50%",
  transform: "translateX(-50%)",
  background: "rgba(10,10,10,0.95)",
  color: "#fff",
  padding: "14px 26px",
  borderRadius: "999px",
  display: "flex",
  alignItems: "center",
  gap: "12px",
  boxShadow: "0 10px 40px rgba(0,0,0,0.35)",
  zIndex: 999999,
  fontSize: "14px",
  fontWeight: 600,
  backdropFilter: "blur(12px)",
};

const iconStyle = {
  width: "26px",
  height: "26px",
  borderRadius: "50%",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  fontSize: "14px",
};
