import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import AllCampaigns from "./components/AllCampaigns";
import CreateCampaign from "./components/CreateCampaign";
import MyCampaigns from "./components/MyCampaigns";
import Dashboard from "./components/Dashboard";
import { useState } from "react";

function App() {
  const [walletAddress, setWalletAddress] = useState("");
  
  // --- MODERN NOTIFICATION STATE ---
  const [toast, setToast] = useState({ show: false, msg: "", type: "success" });

  const showToast = (message, type = "success") => {
    setToast({ show: true, msg: message, type: type });
    // Hide automatically after 3.5 seconds
    setTimeout(() => {
      setToast({ show: false, msg: "", type: "" });
    }, 3500);
  };

  return (
    <Router>
      <div className="App">
        
        {/* --- DYNAMIC ISLAND UI (BOTTOM VERSION) --- */}
        {toast.show && (
          <>
            <style>
              {`
                @keyframes slideUp {
                  0% { transform: translate(-50%, 150%); opacity: 0; }
                  50% { transform: translate(-50%, -15%); opacity: 1; }
                  100% { transform: translate(-50%, 0); opacity: 1; }
                }
                .dynamic-island {
                  animation: slideUp 0.6s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards;
                }
              `}
            </style>
            <div 
              className="dynamic-island"
              style={{
                position: 'fixed',
                bottom: '30px', /* <--- MOVED TO BOTTOM */
                left: '50%',
                transform: 'translateX(-50%)',
                background: 'rgba(5, 5, 5, 0.95)', 
                color: 'white',
                padding: '14px 28px',
                borderRadius: '50px',
                display: 'flex',
                alignItems: 'center',
                gap: '15px',
                boxShadow: '0 10px 40px rgba(0,0,0,0.3)',
                zIndex: 99999999,
                minWidth: '320px',
                justifyContent: 'center',
                backdropFilter: 'blur(12px)',
                border: '1px solid rgba(255,255,255,0.1)',
                fontFamily: "'Inter', sans-serif",
                fontSize: '15px'
              }}
            >
              {/* Icon */}
              <div style={{
                width: '26px', 
                height: '26px', 
                borderRadius: '50%', 
                background: toast.type === 'error' ? '#ef4444' : '#10b981',
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center',
                fontSize: '14px',
                fontWeight: 'bold'
              }}>
                {toast.type === 'error' ? '✕' : '✓'}
              </div>
              
              {/* Message */}
              <span style={{ fontWeight: 600 }}>{toast.msg}</span>
            </div>
          </>
        )}

        <Navbar walletAddress={walletAddress} setWalletAddress={setWalletAddress} />
        
        <Routes>
          <Route path="/" element={<AllCampaigns />} />
          
          <Route 
            path="/create" 
            element={<CreateCampaign showToast={showToast} />} 
          />
          
          <Route path="/mycampaigns" element={<MyCampaigns />} />
          
          <Route 
            path="/campaign/:id" 
            element={<Dashboard showToast={showToast} />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
