import { useState } from "react";
import { ethers } from "ethers";
import { getContract } from "../connectWallet";
import { useNavigate } from "react-router-dom";
import "../css/CreateCampaign.css";
import axios from "axios"; 

// --- 1. CHANGE THIS IMPORT TO REACT-TOASTIFY ---
import { toast } from 'react-toastify'; 
// -----------------------------------------------

// YOUR PINATA JWT KEY
const PINATA_JWT = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..orWGmCCzrfyYGkB0WynSWPepgeDJ_ByuBE30YkrM3wQ";

function CreateCampaign() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [goal, setGoal] = useState("");
  const [imageFile, setImageFile] = useState(null); 
  const [imageUrl, setImageUrl] = useState("");     
  const [isLoading, setIsLoading] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const preview = URL.createObjectURL(file);
      setImageUrl(preview);
    }
  };

  const uploadToIPFS = async (file) => {
    if (!file) return;
    try {
      const formData = new FormData();
      formData.append("file", file);

      const res = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
        headers: {
          'Authorization': `Bearer ${PINATA_JWT}`,
        }
      });
      const ipfsHash = res.data.IpfsHash;
      return `https://gateway.pinata.cloud/ipfs/${ipfsHash}`;
    } catch (error) {
      console.error("Error uploading to Pinata:", error);
      throw new Error("Image upload failed");
    }
  };

  const createCampaign = async () => {
    if (!title || !description || !goal || !imageUrl) {
      // REACT-TOASTIFY ERROR
      toast.error("Please fill in all fields!", {
        position: "top-center"
      });
      return;
    }

    try {
      setIsLoading(true);
      const contract = await getContract();
      if (!contract) return;

      const goalInWei = ethers.utils.parseEther(goal);
      
      // Step A: Upload Image
      let finalUrl = imageUrl;
      if (imageFile) {
        setUploadingImage(true);
        // Toastify Loading
        const toastId = toast.loading("Uploading image to IPFS...");
        
        try {
          finalUrl = await uploadToIPFS(imageFile);
          // Update Toast to Success
          toast.update(toastId, { render: "Image uploaded!", type: "success", isLoading: false, autoClose: 2000 });
        } catch (uploadError) {
          toast.update(toastId, { render: "Image upload failed.", type: "error", isLoading: false, autoClose: 3000 });
          setIsLoading(false);
          setUploadingImage(false);
          return;
        }
        setUploadingImage(false);
      }

      // Step B: Blockchain Transaction
      const txToastId = toast.loading("Waiting for wallet approval...");

      const tx = await contract.createCampaign(
        title,
        description,
        goalInWei,
        finalUrl
      );

      toast.update(txToastId, { render: "Transaction sent! Waiting for confirmation...", isLoading: true });

      await tx.wait();

      // Final Success Message
      toast.update(txToastId, { render: "Campaign Created Successfully! üéâ", type: "success", isLoading: false, autoClose: 5000 });
      
      // Small delay before moving page so user sees the success
      setTimeout(() => {
        navigate("/");
      }, 2000);

    } catch (error) {
      console.error("Error creating campaign:", error);
      // Dismiss all generic loading toasts if error
      toast.dismiss();
      toast.error("Transaction failed! Check console.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="create-container">
      <div className="create-form">
        <h2 className="form-title">Start a Campaign</h2>
        
        <div className="form-group">
          <label>Campaign Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g., Clean Water for Village"
            className="form-input"
          />
        </div>

        <div className="form-group">
          <label>Story</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Tell us about your project..."
            rows="4"
            className="form-textarea"
          />
        </div>

        <div className="form-group">
          <label>Goal Amount (ETH)</label>
          <div className="funding-input-container">
            <input
              type="number"
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              placeholder="0.5"
              className="funding-input"
            />
            <span className="eth-badge">ETH</span>
          </div>
        </div>

        <div className="form-group">
          <label>Campaign Image</label>
          <div className="image-upload-wrapper">
            <input 
              type="file" 
              id="file-upload" 
              className="hidden-file-input"
              accept="image/*"
              onChange={handleFileChange}
            />
            <label htmlFor="file-upload" className="file-upload-box">
              <span className="upload-icon">üìÅ</span>
              <span>Click to Upload from Device</span>
            </label>

            <div className="or-divider">OR</div>

            <input
              type="text"
              value={imageUrl}
              onChange={(e) => {
                setImageUrl(e.target.value);
                setImageFile(null);
              }}
              placeholder="Paste image URL directly..."
              className="form-input"
            />
          </div>

          {imageUrl && (
            <div className="image-preview-container">
              <img src={imageUrl} alt="Preview" className="image-preview" />
              <button 
                className="clear-img-btn"
                onClick={() => { setImageUrl(""); setImageFile(null); }}
              >
                ‚úï
              </button>
            </div>
          )}
        </div>

        <button
          onClick={createCampaign}
          disabled={isLoading || uploadingImage}
          className="create-btn"
        >
          {uploadingImage ? "Uploading Image..." : isLoading ? "Processing..." : "Launch Campaign"}
        </button>
      </div>
    </div>
  );
}

export default CreateCampaign;
