import { useState } from "react";
import { ethers } from "ethers";
import { getContract } from "../connectWallet";
import { useNavigate } from "react-router-dom";
import "../css/CreateCampaign.css";
import axios from "axios"; 

// --- MODERN NOTIFICATIONS ---
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// YOUR PINATA JWT KEY
const PINATA_JWT = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..orWGmCCzrfyYGkB0WynSWPepgeDJ_ByuBE30YkrM3wQ";

function CreateCampaign() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [goal, setGoal] = useState("");
  const [imageFile, setImageFile] = useState(null); 
  const [imageUrl, setImageUrl] = useState("");     
  const [isLoading, setIsLoading] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);
  const navigate = useNavigate();

  // --- 1. HANDLE FILE SELECTION ---
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file size (e.g., limit to 5MB)
      if (file.size > 5 * 1024 * 1024) {
        toast.error("File is too large! Please choose an image under 5MB.");
        return;
      }
      setImageFile(file);
      const preview = URL.createObjectURL(file);
      setImageUrl(preview);
    }
  };

  // --- 2. UPLOAD TO IPFS ---
  const uploadToIPFS = async (file) => {
    if (!file) return;

    try {
      const formData = new FormData();
      formData.append("file", file);

      const res = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
        headers: {
          'Authorization': `Bearer ${PINATA_JWT}`,
        }
      });

      const ipfsHash = res.data.IpfsHash;
      return `https://gateway.pinata.cloud/ipfs/${ipfsHash}`;
      
    } catch (error) {
      console.error("Error uploading to Pinata:", error);
      throw new Error("Image upload failed");
    }
  };

  const createCampaign = async () => {
    // Validation Toast
    if (!title || !description || !goal || !imageUrl) {
      toast.warn("‚ö†Ô∏è Please fill in all fields before launching!");
      return;
    }

    try {
      setIsLoading(true);
      const contract = await getContract();
      if (!contract) {
        toast.error("Wallet not connected! Please connect your wallet.");
        setIsLoading(false);
        return;
      }

      const goalInWei = ethers.utils.parseEther(goal);
      
      // Step A: Upload Image to IPFS
      let finalUrl = imageUrl;
      
      if (imageFile) {
        setUploadingImage(true);
        // Show loading toast for upload
        const uploadToast = toast.loading("Uploading image to IPFS...");
        
        try {
          finalUrl = await uploadToIPFS(imageFile);
          toast.update(uploadToast, { render: "Image uploaded successfully!", type: "success", isLoading: false, autoClose: 2000 });
        } catch (uploadError) {
          toast.update(uploadToast, { render: "Image upload failed. Check internet/key.", type: "error", isLoading: false, autoClose: 3000 });
          setIsLoading(false);
          setUploadingImage(false);
          return;
        }
        setUploadingImage(false);
      }

      // Step B: Send Data to Blockchain
      const tx = await contract.createCampaign(
        title,
        description,
        goalInWei,
        finalUrl 
      );

      // Show transaction pending toast
      toast.info("Transaction sent! Waiting for confirmation...", {
        autoClose: 8000,
        pauseOnHover: true
      });

      await tx.wait();

      // Success Toast
      toast.success("üöÄ Campaign Created Successfully!", {
        position: "top-center",
        autoClose: 4000
      });

      // Delay navigation slightly so user sees the success message
      setTimeout(() => {
        navigate("/");
      }, 2000);

    } catch (error) {
      console.error("Error creating campaign:", error);
      toast.error("Transaction failed! Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="create-container">
      {/* Toast Container needs to be here to render notifications */}
      <ToastContainer 
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={true}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />

      <div className="create-form">
        <h2 className="form-title">Start a Campaign</h2>
        
        {/* Title */}
        <div className="form-group">
          <label>Campaign Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g., Clean Water for Village"
            className="form-input"
          />
        </div>

        {/* Description */}
        <div className="form-group">
          <label>Story</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Tell us about your project..."
            rows="4"
            className="form-textarea"
          />
        </div>

        {/* Goal */}
        <div className="form-group">
          <label>Goal Amount (ETH)</label>
          <div className="funding-input-container">
            <input
              type="number"
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              placeholder="0.5"
              className="funding-input"
              step="0.01"
              min="0"
            />
            <span className="eth-badge">ETH</span>
          </div>
        </div>

        {/* --- IMAGE UPLOAD SECTION --- */}
        <div className="form-group">
          <label>Campaign Image</label>
          
          <div className="image-upload-wrapper">
            <input 
              type="file" 
              id="file-upload" 
              className="hidden-file-input"
              accept="image/*"
              onChange={handleFileChange}
            />
            <label htmlFor="file-upload" className="file-upload-box">
              <span className="upload-icon">üìÅ</span>
              <span>Click to Upload from Device</span>
            </label>

            <div className="or-divider">OR</div>

            <input
              type="text"
              value={imageUrl}
              onChange={(e) => {
                setImageUrl(e.target.value);
                setImageFile(null); 
              }}
              placeholder="Paste image URL directly..."
              className="form-input"
            />
          </div>

          {/* Preview */}
          {imageUrl && (
            <div className="image-preview-container">
              <img src={imageUrl} alt="Preview" className="image-preview" />
              <button 
                className="clear-img-btn"
                onClick={() => { setImageUrl(""); setImageFile(null); }}
              >
                ‚úï
              </button>
            </div>
          )}
        </div>

        <button
          onClick={createCampaign}
          disabled={isLoading || uploadingImage}
          className="create-btn"
        >
          {uploadingImage ? "Uploading Image..." : isLoading ? "Confirming..." : "Launch Campaign"}
        </button>
      </div>
    </div>
  );
}

export default CreateCampaign;
