import { useState } from "react";
import { ethers } from "ethers";
import { getContract } from "../connectWallet";
import { useNavigate } from "react-router-dom";
import "../css/CreateCampaign.css";
import axios from "axios"; 

// --- 1. IMPORT BOUNCE ANIMATION & TOASTIFY ---
import { ToastContainer, toast, Bounce } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const PINATA_JWT = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..orWGmCCzrfyYGkB0WynSWPepgeDJ_ByuBE30YkrM3wQ";

function CreateCampaign() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [goal, setGoal] = useState("");
  const [imageFile, setImageFile] = useState(null); 
  const [imageUrl, setImageUrl] = useState("");     
  const [isLoading, setIsLoading] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);
  const navigate = useNavigate();

  // ... (handleFileChange and uploadToIPFS remain the same as previous code) ...

  // Keep your existing handleFileChange and uploadToIPFS functions here...
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const preview = URL.createObjectURL(file);
      setImageUrl(preview);
    }
  };

  const uploadToIPFS = async (file) => {
    if (!file) return;
    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
        headers: { 'Authorization': `Bearer ${PINATA_JWT}` }
      });
      return `https://gateway.pinata.cloud/ipfs/${res.data.IpfsHash}`;
    } catch (error) {
      throw new Error("Image upload failed");
    }
  };

  const createCampaign = async () => {
    if (!title || !description || !goal || !imageUrl) {
      toast.warn("‚ö†Ô∏è Please fill in all fields!");
      return;
    }

    try {
      setIsLoading(true);
      const contract = await getContract();
      if (!contract) {
        toast.error("Please connect your wallet!");
        setIsLoading(false);
        return;
      }

      const goalInWei = ethers.utils.parseEther(goal);
      let finalUrl = imageUrl;
      
      if (imageFile) {
        setUploadingImage(true);
        const uploadToast = toast.loading("Uploading image to IPFS...");
        
        try {
          finalUrl = await uploadToIPFS(imageFile);
          // Update the loading toast to success
          toast.update(uploadToast, { 
            render: "Image uploaded!", 
            type: "success", 
            isLoading: false, 
            autoClose: 2000,
            transition: Bounce 
          });
        } catch (uploadError) {
          toast.update(uploadToast, { render: "Upload failed.", type: "error", isLoading: false, autoClose: 3000 });
          setIsLoading(false);
          setUploadingImage(false);
          return;
        }
        setUploadingImage(false);
      }

      const tx = await contract.createCampaign(title, description, goalInWei, finalUrl);

      toast.info("Transaction Sent! Waiting...", { autoClose: 5000 });

      await tx.wait();

      toast.success("üöÄ Campaign Created Successfully!", {
        autoClose: 3000
      });

      setTimeout(() => navigate("/"), 2000);

    } catch (error) {
      console.error(error);
      toast.error("Transaction failed!");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="create-container">
      {/* --- 2. UPDATED TOAST CONTAINER CONFIGURATION --- */}
      <ToastContainer 
  position="top-center" // <--- CHANGE THIS
  transition={Bounce} 
  theme="dark"     // Adds the dynamic bounce animation
        autoClose={4000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
        // Optional: Custom style to lift it slightly off the very bottom edge
        style={{ marginBottom: "20px" }} 
      />

      <div className="create-form">
        <h2 className="form-title">Start a Campaign</h2>
        {/* ... Rest of your form inputs (Title, Story, Goal, Image) ... */}
        
        <div className="form-group">
           <label>Campaign Title</label>
           <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} className="form-input" />
        </div>
        
        <div className="form-group">
           <label>Story</label>
           <textarea value={description} onChange={(e) => setDescription(e.target.value)} rows="4" className="form-textarea" />
        </div>

        <div className="form-group">
           <label>Goal (ETH)</label>
           <div className="funding-input-container">
             <input type="number" value={goal} onChange={(e) => setGoal(e.target.value)} className="funding-input" />
             <span className="eth-badge">ETH</span>
           </div>
        </div>

        <div className="form-group">
          <label>Image</label>
          <div className="image-upload-wrapper">
             <input type="file" id="file" className="hidden-file-input" onChange={handleFileChange} />
             <label htmlFor="file" className="file-upload-box">üìÅ Upload Image</label>
             <div className="or-divider">OR</div>
             <input type="text" value={imageUrl} onChange={(e) => { setImageUrl(e.target.value); setImageFile(null); }} placeholder="Image URL" className="form-input" />
          </div>
          {imageUrl && (
            <div className="image-preview-container">
              <img src={imageUrl} alt="preview" className="image-preview" />
              <button className="clear-img-btn" onClick={() => { setImageUrl(""); setImageFile(null); }}>‚úï</button>
            </div>
          )}
        </div>

        <button onClick={createCampaign} disabled={isLoading || uploadingImage} className="create-btn">
          {uploadingImage ? "Uploading..." : isLoading ? "Confirming..." : "Launch Campaign"}
        </button>
      </div>
    </div>
  );
}

export default CreateCampaign;
