import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { connectWallet } from "../connectWallet";
import "../css/Navbar.css";

function Navbar({ walletAddress, setWalletAddress }) {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  const handleConnect = async () => {
    const signer = await connectWallet();
    if (signer) {
      const addr = await signer.getAddress();
      setWalletAddress(addr);
    }
  };

  // Add glass effect on scroll
  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 20);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Auto-Connect Logic
  useEffect(() => {
    const checkConnectedWallet = async () => {
      if (window.ethereum) {
        try {
          const accounts = await window.ethereum.request({ method: 'eth_accounts' });
          if (accounts.length > 0) setWalletAddress(accounts[0]);

          window.ethereum.on('accountsChanged', (accounts) => {
            if (accounts.length > 0) setWalletAddress(accounts[0]);
            else setWalletAddress("");
          });
        } catch (error) {
          console.error("Error checking wallet connection:", error);
        }
      }
    };
    checkConnectedWallet();
  }, [setWalletAddress]);

  const formatAddress = (addr) => addr ? `${addr.slice(0, 6)}...${addr.slice(-4)}` : "Connect Wallet";

  return (
    <nav className={`navbar ${scrolled ? "scrolled" : ""}`}>
      <div className="navbar-container">
        {/* Logo */}
        <div className="navbar-logo">
          <div className="logo-glow"></div>
          <span className="logo-text">CrowdFund.</span>
        </div>

        {/* Links */}
        <div className={`navbar-links ${isOpen ? "active" : ""}`}>
          <NavLink to="/" className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")} onClick={() => setIsOpen(false)}>
            Explore
          </NavLink>
          <NavLink to="/create" className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")} onClick={() => setIsOpen(false)}>
            Start Campaign
          </NavLink>
          <NavLink to="/mycampaigns" className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")} onClick={() => setIsOpen(false)}>
            My Dashboard
          </NavLink>
          
          {/* Mobile Connect Button */}
          <button className="connect-btn mobile-only" onClick={handleConnect}>
            {formatAddress(walletAddress)}
          </button>
        </div>

        {/* Desktop Actions */}
        <div className="navbar-actions">
          <button className={`connect-btn desktop-only ${walletAddress ? "connected" : ""}`} onClick={handleConnect}>
            {walletAddress && <span className="status-dot"></span>}
            {formatAddress(walletAddress)}
          </button>

          <div className="hamburger" onClick={toggleMenu}>
            <div className={`bar ${isOpen ? "open" : ""}`}></div>
            <div className={`bar ${isOpen ? "open" : ""}`}></div>
            <div className={`bar ${isOpen ? "open" : ""}`}></div>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
