import { useState } from "react";
import { ethers } from "ethers";
import { getContract } from "../connectWallet";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/CreateCampaign.css";

/* ================= PINATA ================= */

const PINATA_JWT = "PASTE_YOUR_PINATA_JWT_HERE";

/* ================= COMPONENT ================= */

function CreateCampaign({ showToast }) {
  const navigate = useNavigate();

  /* ---------- FORM STATE ---------- */
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [goal, setGoal] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState("");

  /* ---------- UI STATE ---------- */
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);

  /* ================= HELPERS ================= */

  const notify = (msg, type = "success") => {
    if (showToast) showToast(msg, type);
  };

  const handleImageSelect = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setImageFile(file);
    setImagePreview(URL.createObjectURL(file));
  };

  const uploadToIPFS = async (file) => {
    const formData = new FormData();
    formData.append("file", file);

    const res = await axios.post(
      "https://api.pinata.cloud/pinning/pinFileToIPFS",
      formData,
      {
        headers: {
          Authorization: `Bearer ${PINATA_JWT}`,
        },
      }
    );

    return `https://gateway.pinata.cloud/ipfs/${res.data.IpfsHash}`;
  };

  /* ================= MAIN ACTION ================= */

  const handleCreateCampaign = async () => {
    /* ---------- VALIDATION ---------- */
    if (!title.trim() || !description.trim() || !goal || !imagePreview) {
      notify("All fields are required", "error");
      return;
    }

    if (Number(goal) <= 0) {
      notify("Goal must be greater than 0 ETH", "error");
      return;
    }

    try {
      setLoading(true);

      /* ---------- CONTRACT ---------- */
      const contract = await getContract();
      if (!contract) {
        notify("Wallet not connected", "error");
        return;
      }

      /* ---------- IMAGE UPLOAD ---------- */
      let finalImageUrl = imagePreview;

      if (imageFile) {
        setUploading(true);
        notify("Uploading image to IPFS‚Ä¶", "success");

        try {
          finalImageUrl = await uploadToIPFS(imageFile);
        } catch {
          notify("Image upload failed", "error");
          return;
        } finally {
          setUploading(false);
        }
      }

      /* ---------- TRANSACTION ---------- */
      notify("Confirm transaction in wallet", "success");

      const tx = await contract.createCampaign(
        title,
        description,
        ethers.utils.parseEther(goal),
        finalImageUrl
      );

      notify("Transaction sent. Waiting for confirmation‚Ä¶", "success");
      await tx.wait();

      notify("üéâ Campaign created successfully!", "success");

      /* ---------- RESET + REDIRECT ---------- */
      setTimeout(() => {
        navigate("/");
      }, 1500);

    } catch (err) {
      console.error(err);
      notify("Transaction failed", "error");
    } finally {
      setLoading(false);
    }
  };

  /* ================= UI ================= */

  return (
    <div className="create-container">
      <div className="create-form">

        <h2 className="form-title">Start a Campaign</h2>

        {/* TITLE */}
        <div className="form-group">
          <label>Campaign Title</label>
          <input
            className="form-input"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Build something meaningful"
          />
        </div>

        {/* DESCRIPTION */}
        <div className="form-group">
          <label>Story</label>
          <textarea
            className="form-textarea"
            rows="5"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Explain your idea clearly and honestly"
          />
        </div>

        {/* GOAL */}
        <div className="form-group">
          <label>Goal Amount</label>
          <div className="funding-input-container">
            <input
              type="number"
              className="funding-input"
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              placeholder="0.5"
            />
            <span className="eth-badge">ETH</span>
          </div>
        </div>

        {/* IMAGE */}
        <div className="form-group">
          <label>Campaign Image</label>

          <div className="image-upload-wrapper">
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              className="hidden-file-input"
              onChange={handleImageSelect}
            />

            <label htmlFor="image-upload" className="file-upload-box">
              <span className="upload-icon">üìÅ</span>
              <span>Upload from device</span>
            </label>

            <div className="or-divider">OR</div>

            <input
              className="form-input"
              placeholder="Paste image URL"
              value={imagePreview}
              onChange={(e) => {
                setImagePreview(e.target.value);
                setImageFile(null);
              }}
            />
          </div>

          {imagePreview && (
            <div className="image-preview-container">
              <img src={imagePreview} alt="Preview" className="image-preview" />
              <button
                className="clear-img-btn"
                onClick={() => {
                  setImagePreview("");
                  setImageFile(null);
                }}
              >
                ‚úï
              </button>
            </div>
          )}
        </div>

        {/* SUBMIT */}
        <button
          className="create-btn"
          onClick={handleCreateCampaign}
          disabled={loading || uploading}
        >
          {uploading
            ? "Uploading Image‚Ä¶"
            : loading
            ? "Processing‚Ä¶"
            : "Launch Campaign"}
        </button>

      </div>
    </div>
  );
}

export default CreateCampaign;
