import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { connectWallet } from "../connectWallet";
import "../css/Navbar.css";

function Navbar({ walletAddress, setWalletAddress }) {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleConnect = async () => {
    const signer = await connectWallet();
    if (signer) {
      const addr = await signer.getAddress();
      setWalletAddress(addr);
    }
  };

  // --- NEW: AUTO-CONNECT LOGIC ---
  useEffect(() => {
    const checkConnectedWallet = async () => {
      if (window.ethereum) {
        try {
          // 1. Check if we are already connected
          const accounts = await window.ethereum.request({ method: 'eth_accounts' });
          if (accounts.length > 0) {
            setWalletAddress(accounts[0]);
          }

          // 2. Listen for account changes (e.g. user switches wallet in MetaMask)
          window.ethereum.on('accountsChanged', (accounts) => {
            if (accounts.length > 0) {
              setWalletAddress(accounts[0]);
            } else {
              setWalletAddress(""); // User disconnected
            }
          });

        } catch (error) {
          console.error("Error checking wallet connection:", error);
        }
      }
    };

    checkConnectedWallet();
  }, [setWalletAddress]);
  // -------------------------------

  // Helper to format address (e.g. 0x123...abcd)
  const formatAddress = (addr) => {
    return addr ? `${addr.slice(0, 6)}...${addr.slice(-4)}` : "Connect Wallet";
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* 1. Logo Section */}
        <div className="navbar-logo">
          <span className="logo-text">CrowdFund.</span>
        </div>

        {/* 2. Desktop Menu & Mobile Dropdown */}
        <div className={`navbar-links ${isOpen ? "active" : ""}`}>
          <NavLink 
            to="/" 
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
            onClick={() => setIsOpen(false)} 
          >
            All Campaigns
          </NavLink>
          <NavLink 
            to="/create" 
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
            onClick={() => setIsOpen(false)}
          >
            Create Campaign
          </NavLink>
          <NavLink 
            to="/mycampaigns" 
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
            onClick={() => setIsOpen(false)}
          >
            My Campaigns
          </NavLink>

          {/* Mobile Connect Button */}
          <div className="mobile-btn-container">
            <button className="connect-btn mobile-only" onClick={handleConnect}>
              {formatAddress(walletAddress)}
            </button>
          </div>
        </div>

        {/* 3. Actions (Desktop Connect Button + Hamburger) */}
        <div className="navbar-actions">
          <button className="connect-btn desktop-only" onClick={handleConnect}>
            {formatAddress(walletAddress)}
          </button>

          {/* Hamburger Icon */}
          <div className="hamburger" onClick={toggleMenu}>
            {isOpen ? (
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <line x1="18" y1="6" x2="6" y2="18"></line>
                <line x1="6" y1="6" x2="18" y2="18"></line>
              </svg>
            ) : (
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <line x1="3" y1="12" x2="21" y2="12"></line>
                <line x1="3" y1="6" x2="21" y2="6"></line>
                <line x1="3" y1="18" x2="21" y2="18"></line>
              </svg>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
