import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { getContract, connectWallet } from "../connectWallet";
import "../css/Dashboard.css";

function Dashboard({ showToast }) {
  const { id } = useParams();

  const [campaign, setCampaign] = useState(null);
  const [fundAmount, setFundAmount] = useState("");
  const [userAddress, setUserAddress] = useState("");
  const [isFunding, setIsFunding] = useState(false);
  const [isWithdrawing, setIsWithdrawing] = useState(false);
  const [transactions, setTransactions] = useState([]);

  // ---------------- FETCH DATA ----------------
  const fetchData = async () => {
    try {
      const contract = await getContract();
      const campaigns = await contract.getAllCampaigns();
      const data = campaigns[id];
      if (!data) return;

      const goal = ethers.utils.formatEther(data.goal);
      const raised = ethers.utils.formatEther(data.fundsRaised);

      setCampaign({
        owner: data.owner.toLowerCase(),
        title: data.title,
        description: data.description,
        imageUrl: data.imageUrl,
        goal,
        raised,
        completed: data.completed,
        progress: Math.min((raised / goal) * 100, 100),
        createdAt: new Date(data.createdAt.toNumber() * 1000).toLocaleDateString(),
      });

      const signer = await connectWallet();
      if (signer) {
        setUserAddress((await signer.getAddress()).toLowerCase());
      }

      const log = await contract.getTransactionLog(id);
      setTransactions(
        log.map((tx, i) => ({
          id: i,
          sender: tx.sender.toLowerCase(),
          amount: ethers.utils.formatEther(tx.amount),
          type: tx.txType.toString() === "0" ? "Donation" : "Withdrawal",
          time: new Date(tx.timestamp * 1000).toLocaleDateString(),
        })).reverse()
      );
    } catch (err) {
      console.error(err);
      showToast("Failed to load campaign", "error");
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  // ---------------- FUND ----------------
  const fundCampaign = async () => {
    if (!fundAmount || Number(fundAmount) <= 0) {
      showToast("Enter a valid ETH amount", "error");
      return;
    }

    if (campaign.owner === userAddress) {
      showToast("You cannot fund your own campaign", "error");
      return;
    }

    try {
      setIsFunding(true);
      const contract = await getContract();

      showToast("Confirm transaction in wallet");
      const tx = await contract.fundCampaign(id, {
        value: ethers.utils.parseEther(fundAmount),
      });

      await tx.wait();
      showToast("ðŸŽ‰ Donation successful");
      setFundAmount("");
      fetchData();
    } catch (err) {
      console.error(err);
      showToast("Transaction failed", "error");
    } finally {
      setIsFunding(false);
    }
  };

  // ---------------- WITHDRAW ----------------
  const withdrawFunds = async () => {
    try {
      setIsWithdrawing(true);
      const contract = await getContract();
      const tx = await contract.withdrawFunds(id);
      await tx.wait();
      showToast("Funds withdrawn");
      fetchData();
    } catch (err) {
      console.error(err);
      showToast("Withdrawal failed", "error");
    } finally {
      setIsWithdrawing(false);
    }
  };

  if (!campaign) return <div>Loading campaign...</div>;

  const isOwner = campaign.owner === userAddress;
  const goalReached = Number(campaign.raised) >= Number(campaign.goal);

  return (
    <div className="dashboard-wrapper">
      <img src={campaign.imageUrl} alt={campaign.title} />

      <h1>{campaign.title}</h1>
      <p>{campaign.description}</p>

      <p>{campaign.raised} / {campaign.goal} ETH</p>

      {!goalReached && !isOwner && (
        <>
          <input
            type="number"
            placeholder="0.1"
            value={fundAmount}
            onChange={(e) => setFundAmount(e.target.value)}
          />
          <button
            type="button"
            onClick={fundCampaign}
            disabled={isFunding}
          >
            {isFunding ? "Processing..." : "Back this Project"}
          </button>
        </>
      )}

      {isOwner && goalReached && (
        <button
          type="button"
          onClick={withdrawFunds}
          disabled={isWithdrawing}
        >
          {isWithdrawing ? "Processing..." : "Withdraw Funds"}
        </button>
      )}
    </div>
  );
}

export default Dashboard;
