import { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { getContract, connectWallet } from "../connectWallet";
import "../css/Dashboard.css";

function Dashboard({ showToast }) {
  const { id } = useParams();

  /* ================= STATE ================= */

  const [campaign, setCampaign] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [userAddress, setUserAddress] = useState("");

  const [fundAmount, setFundAmount] = useState("");
  const [activeTab, setActiveTab] = useState("story");

  const [funding, setFunding] = useState(false);
  const [withdrawing, setWithdrawing] = useState(false);

  /* ================= HELPERS ================= */

  const notify = (msg, type = "success") => {
    if (showToast) showToast(msg, type);
  };

  const isOwner =
    campaign && userAddress && campaign.owner === userAddress;

  const goalReached =
    campaign &&
    Number(campaign.fundsRaised) >= Number(campaign.goal);

  /* ================= FETCH ================= */

  const fetchCampaign = useCallback(async () => {
    try {
      const contract = await getContract();
      if (!contract) return;

      const campaigns = await contract.getAllCampaigns();
      const data = campaigns[id];
      if (!data) return;

      const goal = ethers.utils.formatEther(data.goal);
      const raised = ethers.utils.formatEther(data.fundsRaised);

      setCampaign({
        id,
        owner: data.owner.toLowerCase(),
        title: data.title,
        description: data.description,
        goal,
        fundsRaised: raised,
        completed: data.completed,
        imageUrl: data.imageUrl,
        progress: Math.min((raised / goal) * 100, 100),
        createdAt: new Date(data.createdAt.toNumber() * 1000).toLocaleDateString(),
      });

      const log = await contract.getTransactionLog(id);
      setTransactions(
        log
          .map((tx, i) => ({
            id: i,
            sender: tx.sender.toLowerCase(),
            amount: ethers.utils.formatEther(tx.amount),
            type: tx.txType.toString() === "0" ? "Donation" : "Withdrawal",
            date: new Date(Number(tx.timestamp) * 1000).toLocaleDateString(),
          }))
          .reverse()
      );

      const signer = await connectWallet();
      if (signer) {
        const addr = await signer.getAddress();
        setUserAddress(addr.toLowerCase());
      }
    } catch (err) {
      console.error(err);
      notify("Failed to load campaign", "error");
    }
  }, [id, showToast]);

  useEffect(() => {
    fetchCampaign();
  }, [fetchCampaign]);

  /* ================= ACTIONS ================= */

  const handleFund = async () => {
    if (!fundAmount || Number(fundAmount) <= 0) {
      notify("Enter a valid amount", "error");
      return;
    }

    if (isOwner) {
      notify("You cannot fund your own campaign", "error");
      return;
    }

    try {
      setFunding(true);
      const contract = await getContract();

      notify("Confirm transaction in wallet");
      const tx = await contract.fundCampaign(id, {
        value: ethers.utils.parseEther(fundAmount),
      });

      notify("Transaction sent. Waiting‚Ä¶");
      await tx.wait();

      notify("üéâ Donation successful!");
      setFundAmount("");
      fetchCampaign();
    } catch (err) {
      console.error(err);
      notify("Donation failed", "error");
    } finally {
      setFunding(false);
    }
  };

  const handleWithdraw = async () => {
    try {
      setWithdrawing(true);
      const contract = await getContract();

      notify("Confirm withdrawal in wallet");
      const tx = await contract.withdrawFunds(id);
      await tx.wait();

      notify("üí∞ Funds withdrawn successfully");
      fetchCampaign();
    } catch (err) {
      console.error(err);
      notify("Withdrawal failed", "error");
    } finally {
      setWithdrawing(false);
    }
  };

  /* ================= LOADING ================= */

  if (!campaign) {
    return <div className="loading-screen">Loading campaign‚Ä¶</div>;
  }

  /* ================= UI ================= */

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-container">

        {/* ================= LEFT ================= */}
        <div className="main-content">
          <div className="hero-image-container">
            <img src={campaign.imageUrl} alt={campaign.title} />
          </div>

          <div className="tabs-header">
            <button
              className={`tab-btn ${activeTab === "story" ? "active" : ""}`}
              onClick={() => setActiveTab("story")}
            >
              Story
            </button>
            <button
              className={`tab-btn ${activeTab === "donors" ? "active" : ""}`}
              onClick={() => setActiveTab("donors")}
            >
              Activity ({transactions.filter(t => t.type === "Donation").length})
            </button>
          </div>

          <div className="tab-content">
            {activeTab === "story" ? (
              <div className="story-text">
                <h3>About this campaign</h3>
                <p>{campaign.description}</p>

                <div className="creator-badge">
                  <div className="creator-avatar" />
                  <div>
                    <span className="creator-label">Organizer</span>
                    <span className="creator-name">
                      {campaign.owner.slice(0, 6)}‚Ä¶{campaign.owner.slice(-4)}
                      {isOwner && " (You)"}
                    </span>
                  </div>
                </div>
              </div>
            ) : (
              <div className="donors-list">
                {transactions.length === 0 && (
                  <p className="empty-muted">No activity yet</p>
                )}

                {transactions.map(tx => (
                  <div key={tx.id} className="donor-row">
                    <div className="donor-avatar-placeholder">
                      {tx.type === "Donation" ? "‚ù§Ô∏è" : "üí∏"}
                    </div>
                    <div className="donor-info">
                      <span className="donor-address">
                        {tx.type}
                        <span className="address-sub">
                          ({tx.sender.slice(0, 6)}‚Ä¶)
                        </span>
                      </span>
                      <span className="donor-date">{tx.date}</span>
                    </div>
                    <div className="donor-amount">
                      {tx.type === "Donation" ? "+" : "-"}
                      {tx.amount} ETH
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* ================= RIGHT ================= */}
        <div className="sidebar-content">
          <div className="funding-card">

            <div className="funding-header">
              <span className={`funding-status ${campaign.completed ? "closed" : "open"}`}>
                {campaign.completed ? "Ended" : "Live"}
              </span>
              <span className="funding-date">
                Created {campaign.createdAt}
              </span>
            </div>

            <h1 className="campaign-title">{campaign.title}</h1>

            <div className="progress-section">
              <div className="progress-bar-bg">
                <div
                  className="progress-bar-fill"
                  style={{ width: `${campaign.progress}%` }}
                />
              </div>

              <div className="stats-grid">
                <div>
                  <span className="stat-value">
                    {campaign.fundsRaised} <small>ETH</small>
                  </span>
                  <span className="stat-label">Raised</span>
                </div>
                <div>
                  <span className="stat-value">
                    {campaign.goal} <small>ETH</small>
                  </span>
                  <span className="stat-label">Goal</span>
                </div>
              </div>
            </div>

            {!campaign.completed && !goalReached && !isOwner && (
              <div className="action-box">
                <label>Pledge amount</label>
                <input
                  type="number"
                  placeholder="0.1"
                  value={fundAmount}
                  onChange={(e) => setFundAmount(e.target.value)}
                />
                <button onClick={handleFund} disabled={funding}>
                  {funding ? "Processing‚Ä¶" : "Back this project"}
                </button>
              </div>
            )}

            {isOwner && goalReached && (
              <button
                className="withdraw-btn"
                onClick={handleWithdraw}
                disabled={withdrawing}
              >
                {withdrawing ? "Processing‚Ä¶" : "Withdraw funds"}
              </button>
            )}
          </div>
        </div>

      </div>
    </div>
  );
}

export default Dashboard;
