import { useState } from "react";
import { ethers } from "ethers";
import { getContract } from "../connectWallet";
import { useNavigate } from "react-router-dom";
import "../css/CreateCampaign.css"; // We will update this CSS too
import axios from "axios"; // You might need to run: npm install axios

function CreateCampaign() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [goal, setGoal] = useState("");
  const [imageFile, setImageFile] = useState(null); // Store the actual file
  const [imageUrl, setImageUrl] = useState("");     // Store the URL (manual or uploaded)
  const [isLoading, setIsLoading] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);
  const navigate = useNavigate();

  // --- 1. HANDLE FILE SELECTION ---
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      // Create a local preview URL so the user sees what they picked immediately
      const preview = URL.createObjectURL(file);
      setImageUrl(preview);
    }
  };

  // --- 2. UPLOAD TO IPFS (Pinata) ---
  const uploadToIPFS = async (file) => {
    if (!file) return;
    
    // NOTE: For this to work, you need a free API Key from Pinata (https://pinata.cloud)
    // If you don't have one yet, this part will just return the local preview URL
    // so you can test the UI design.
    
    /* // REAL IMPLEMENTATION (Uncomment when you have a Key):
    const formData = new FormData();
    formData.append("file", file);

    const res = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
      headers: {
        'Content-Type': `multipart/form-data; boundary=${formData._boundary}`,
        'Authorization': `Bearer PASTE_YOUR_PINATA_JWT_HERE`
      }
    });
    return `https://gateway.pinata.cloud/ipfs/${res.data.IpfsHash}`;
    */

    // MOCK (Remove this when you have a real key):
    console.log("Simulating upload... (You need a Pinata Key for real uploads)");
    return "https://placehold.co/600x400?text=Uploaded+Image"; 
  };

  const createCampaign = async () => {
    if (!title || !description || !goal || !imageUrl) {
      alert("Please fill in all fields!");
      return;
    }

    try {
      setIsLoading(true);
      const contract = await getContract();
      if (!contract) return;

      const goalInWei = ethers.utils.parseEther(goal);
      
      // Step A: If user picked a file, upload it first
      let finalUrl = imageUrl;
      if (imageFile) {
        setUploadingImage(true);
        try {
          finalUrl = await uploadToIPFS(imageFile);
        } catch (uploadError) {
          console.error("Upload failed", uploadError);
          alert("Image upload failed. Please try a manual URL.");
          setIsLoading(false);
          setUploadingImage(false);
          return;
        }
        setUploadingImage(false);
      }

      // Step B: Send to Blockchain
      const tx = await contract.createCampaign(
        title,
        description,
        goalInWei,
        finalUrl // We send the IPFS URL, not the file!
      );

      await tx.wait();

      alert("Campaign Created Successfully!");
      navigate("/");
    } catch (error) {
      console.error("Error creating campaign:", error);
      alert("Transaction failed! (Check console)");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="create-container">
      <div className="create-form">
        <h2 className="form-title">Start a Campaign</h2>
        
        {/* Title */}
        <div className="form-group">
          <label>Campaign Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g., AI Powered Robotic Arm"
            className="form-input"
          />
        </div>

        {/* Description */}
        <div className="form-group">
          <label>Story</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Tell us about your project..."
            rows="4"
            className="form-textarea"
          />
        </div>

        {/* Goal */}
        <div className="form-group">
          <label>Goal Amount (ETH)</label>
          <div className="funding-input-container">
            <input
              type="number"
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              placeholder="0.5"
              className="funding-input"
            />
            <span className="eth-badge">ETH</span>
          </div>
        </div>

        {/* --- IMAGE UPLOAD SECTION --- */}
        <div className="form-group">
          <label>Campaign Image</label>
          
          <div className="image-upload-wrapper">
            {/* Option A: File Input (Hidden, triggered by label) */}
            <input 
              type="file" 
              id="file-upload" 
              className="hidden-file-input"
              accept="image/*"
              onChange={handleFileChange}
            />
            <label htmlFor="file-upload" className="file-upload-box">
              <span className="upload-icon">üìÅ</span>
              <span>Click to Upload from Device</span>
            </label>

            <div className="or-divider">OR</div>

            {/* Option B: Manual URL */}
            <input
              type="text"
              value={imageUrl}
              onChange={(e) => {
                setImageUrl(e.target.value);
                setImageFile(null); // Clear file if typing manual URL
              }}
              placeholder="Paste image URL directly..."
              className="form-input"
            />
          </div>

          {/* Preview */}
          {imageUrl && (
            <div className="image-preview-container">
              <img src={imageUrl} alt="Preview" className="image-preview" />
              <button 
                className="clear-img-btn"
                onClick={() => { setImageUrl(""); setImageFile(null); }}
              >
                ‚úï
              </button>
            </div>
          )}
        </div>

        <button
          onClick={createCampaign}
          disabled={isLoading}
          className="create-btn"
        >
          {uploadingImage ? "Uploading Image..." : isLoading ? "Creating Campaign..." : "Launch Campaign"}
        </button>
      </div>
    </div>
  );
}

export default CreateCampaign;
