import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { connectWallet } from "../connectWallet";
import "../css/Navbar.css";

function Navbar({ walletAddress, setWalletAddress }) {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const location = useLocation(); // Used to highlight the active link

  const handleConnect = async () => {
    const signer = await connectWallet();
    if (signer) {
      const addr = await signer.getAddress();
      setWalletAddress(addr);
    }
  };

  const toggleMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Helper to close menu when a link is clicked
  const closeMenu = () => setIsMobileMenuOpen(false);

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* Logo / Brand Name */}
        <Link to="/" className="navbar-logo" onClick={closeMenu}>
          Crowd<span className="logo-accent">Fund</span>
        </Link>

        {/* Mobile Menu Icon (Hamburger) */}
        <div className="menu-icon" onClick={toggleMenu}>
          <div className={isMobileMenuOpen ? "bar open" : "bar"}></div>
          <div className={isMobileMenuOpen ? "bar open" : "bar"}></div>
          <div className={isMobileMenuOpen ? "bar open" : "bar"}></div>
        </div>

        {/* Navigation Links */}
        <div className={isMobileMenuOpen ? "nav-menu active" : "nav-menu"}>
          <Link 
            to="/" 
            className={`nav-link ${location.pathname === "/" ? "active-link" : ""}`} 
            onClick={closeMenu}
          >
            All Campaigns
          </Link>
          <Link 
            to="/create" 
            className={`nav-link ${location.pathname === "/create" ? "active-link" : ""}`} 
            onClick={closeMenu}
          >
            Create Campaign
          </Link>
          <Link 
            to="/mycampaigns" 
            className={`nav-link ${location.pathname === "/mycampaigns" ? "active-link" : ""}`} 
            onClick={closeMenu}
          >
            My Campaigns
          </Link>

          {/* Connect Button (Inside menu for mobile, distinct for desktop via CSS) */}
          <div className="nav-btn-container">
            <button className="connect-btn" onClick={handleConnect}>
              {walletAddress
                ? `${walletAddress.slice(0, 6)}...${walletAddress.slice(-4)}`
                : "Connect Wallet"}
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
