import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getContract, connectWallet } from "../connectWallet";
import { ethers } from "ethers";
import confetti from "canvas-confetti";
import "../css/Dashboard.css";

function Dashboard({ showToast }) {
  const { id } = useParams();

  const [campaign, setCampaign] = useState(null);
  const [fundAmount, setFundAmount] = useState("");
  const [userAddress, setUserAddress] = useState("");
  const [isFunding, setIsFunding] = useState(false);
  const [isWithdrawing, setIsWithdrawing] = useState(false);
  const [transactionLog, setTransactionLog] = useState([]);
  const [activeTab, setActiveTab] = useState("story");
  const [pulse, setPulse] = useState(false);

  const notify = (msg, type = "success") => {
    if (showToast) showToast(msg, type);
  };

  const fireConfetti = () => {
    confetti({
      particleCount: 140,
      spread: 80,
      origin: { y: 0.6 },
      colors: ["#6366f1", "#22d3ee", "#22c55e", "#f59e0b"]
    });
  };

  const fetchData = async () => {
    try {
      const contract = await getContract();
      if (!contract) return;

      const data = (await contract.getAllCampaigns())[id];
      if (!data) return;

      const goal = ethers.utils.formatEther(data.goal);
      const raised = ethers.utils.formatEther(data.fundsRaised);

      setCampaign({
        id,
        owner: data.owner.toLowerCase(),
        title: data.title,
        description: data.description,
        goal,
        fundsRaised: raised,
        completed: data.completed,
        imageUrl: data.imageUrl,
        progress: Math.min((raised / goal) * 100, 100),
        createdAt: new Date(data.createdAt.toNumber() * 1000).toLocaleDateString()
      });

      const log = await contract.getTransactionLog(id);
      setTransactionLog(
        log.map((tx, i) => ({
          id: i,
          sender: tx.sender.toLowerCase(),
          amount: ethers.utils.formatEther(tx.amount),
          type: tx.txType.toString() === "0" ? "Donation" : "Withdrawal",
          date: new Date(tx.timestamp * 1000).toLocaleDateString()
        })).reverse()
      );

      const signer = await connectWallet();
      if (signer) setUserAddress((await signer.getAddress()).toLowerCase());

    } catch (e) {
      console.error(e);
      notify("Failed to load campaign", "error");
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  const fundCampaign = async () => {
    if (!fundAmount) return notify("Enter donation amount", "error");
    if (campaign.owner === userAddress)
      return notify("Owner cannot fund own campaign", "error");

    try {
      setIsFunding(true);
      notify("Confirm transaction in wallet");

      const contract = await getContract();
      const tx = await contract.fundCampaign(id, {
        value: ethers.utils.parseEther(fundAmount)
      });

      notify("Transaction sent‚Ä¶");
      await tx.wait();

      fireConfetti();
      setPulse(true);
      setTimeout(() => setPulse(false), 900);

      notify("Donation successful üéâ");
      setFundAmount("");
      fetchData();

    } catch {
      notify("Transaction failed", "error");
    } finally {
      setIsFunding(false);
    }
  };

  const withdrawFunds = async () => {
    try {
      setIsWithdrawing(true);
      notify("Withdrawing funds‚Ä¶");

      const tx = await (await getContract()).withdrawFunds(id);
      await tx.wait();

      fireConfetti();
      notify("Funds withdrawn successfully");
      fetchData();

    } catch {
      notify("Withdrawal failed", "error");
    } finally {
      setIsWithdrawing(false);
    }
  };

  if (!campaign) return <div className="loading-screen">Loading Campaign‚Ä¶</div>;

  const isOwner = campaign.owner === userAddress;
  const goalReached = Number(campaign.fundsRaised) >= Number(campaign.goal);

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-container">

        {/* LEFT */}
        <div className="main-content">
          <div className="hero-image-container">
            <img src={campaign.imageUrl} className="hero-image" />
            <span className="category-tag">Campaign</span>
          </div>

          <div className="tabs-header">
            <button className={`tab-btn ${activeTab==="story"?"active":""}`} onClick={()=>setActiveTab("story")}>Story</button>
            <button className={`tab-btn ${activeTab==="donors"?"active":""}`} onClick={()=>setActiveTab("donors")}>
              Donors ({transactionLog.filter(t=>t.type==="Donation").length})
            </button>
          </div>

          {activeTab === "story" ? (
            <div className="story-text">
              <h3 className="story-heading">About</h3>
              <p>{campaign.description}</p>
            </div>
          ) : (
            <div className="donors-list">
              {transactionLog.map(tx => (
                <div key={tx.id} className="donor-row">
                  <div className="donor-avatar-placeholder">{tx.type==="Donation"?"‚ù§Ô∏è":"üí∏"}</div>
                  <div>
                    <span className="donor-address">{tx.sender.slice(0,6)}‚Ä¶</span>
                    <span className="donor-date">{tx.date}</span>
                  </div>
                  <div className="donor-amount">
                    {tx.type==="Donation"?"+":"-"}{tx.amount} ETH
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* RIGHT */}
        <div className="sidebar-content">
          <div className={`funding-card ${pulse ? "pulse" : ""}`}>
            <h1 className="campaign-title">{campaign.title}</h1>

            <div className="progress-bar-bg">
              <div className="progress-bar-fill" style={{ width: `${campaign.progress}%` }} />
            </div>

            <div className="stats-grid">
              <div>
                <span className="stat-value">{campaign.fundsRaised} ETH</span>
                <span className="stat-label">Raised</span>
              </div>
              <div>
                <span className="stat-value">{campaign.goal} ETH</span>
                <span className="stat-label">Goal</span>
              </div>
            </div>

            {!goalReached && !isOwner && (
              <div className="action-box">
                <input
                  className="amount-input"
                  placeholder="0.1"
                  value={fundAmount}
                  onChange={e => setFundAmount(e.target.value)}
                />
                <button className="donate-btn" onClick={fundCampaign} disabled={isFunding}>
                  {isFunding ? "Processing‚Ä¶" : "Donate"}
                </button>
              </div>
            )}

            {isOwner && goalReached && (
              <button className="withdraw-btn" onClick={withdrawFunds} disabled={isWithdrawing}>
                {isWithdrawing ? "Processing‚Ä¶" : "Withdraw Funds"}
              </button>
            )}
          </div>
        </div>

      </div>
    </div>
  );
}

export default Dashboard;
