import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { connectWallet } from "../connectWallet";
import { useTheme } from "../theme/ThemeContext";
import "../css/Navbar.css";

function Navbar({ walletAddress, setWalletAddress }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const { theme, toggleTheme } = useTheme();

  // Wallet connect
  const handleConnect = async () => {
    const signer = await connectWallet();
    if (signer) {
      const addr = await signer.getAddress();
      setWalletAddress(addr);
    }
  };

  // Auto connect & account change
  useEffect(() => {
    if (!window.ethereum) return;

    window.ethereum.request({ method: "eth_accounts" }).then(accounts => {
      if (accounts.length) setWalletAddress(accounts[0]);
    });

    window.ethereum.on("accountsChanged", accounts => {
      setWalletAddress(accounts[0] || "");
    });
  }, [setWalletAddress]);

  // Scroll glass effect
  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 20);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const shortAddr = walletAddress
    ? `${walletAddress.slice(0, 6)}...${walletAddress.slice(-4)}`
    : "Connect Wallet";

  return (
    <nav className={`navbar ${scrolled ? "scrolled" : ""}`}>
      <div className="navbar-container">
        
        {/* LOGO */}
        <div className="navbar-logo">
          <span className="logo-text">CrowdFund.</span>
        </div>

        {/* LINKS */}
        <div className={`navbar-links ${menuOpen ? "active" : ""}`}>
          <NavLink to="/" className="nav-link" onClick={() => setMenuOpen(false)}>
            Explore
          </NavLink>
          <NavLink to="/create" className="nav-link" onClick={() => setMenuOpen(false)}>
            Start Campaign
          </NavLink>
          <NavLink to="/mycampaigns" className="nav-link" onClick={() => setMenuOpen(false)}>
            My Dashboard
          </NavLink>

          {/* Mobile actions */}
          <button className="theme-toggle-btn mobile-only" onClick={toggleTheme}>
            {theme === "dark" ? "‚òÄÔ∏è Light" : "üåô Dark"}
          </button>

          <button className="connect-btn mobile-only" onClick={handleConnect}>
            {shortAddr}
          </button>
        </div>

        {/* ACTIONS */}
        <div className="navbar-actions">
          <button className="theme-toggle-btn desktop-only" onClick={toggleTheme}>
            {theme === "dark" ? "‚òÄÔ∏è" : "üåô"}
          </button>

          <button className="connect-btn desktop-only" onClick={handleConnect}>
            {walletAddress && <span className="status-dot" />}
            {shortAddr}
          </button>

          <div className="hamburger" onClick={() => setMenuOpen(!menuOpen)}>
            <span className={menuOpen ? "open" : ""}></span>
            <span className={menuOpen ? "open" : ""}></span>
            <span className={menuOpen ? "open" : ""}></span>
          </div>
        </div>

      </div>
    </nav>
  );
}

export default Navbar;
