import { useState } from "react";
import { ethers } from "ethers";
import { getContract } from "../connectWallet";
import { useNavigate } from "react-router-dom";
import "../css/CreateCampaign.css";
import axios from "axios"; 

// YOUR PINATA KEY
const PINATA_JWT = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..orWGmCCzrfyYGkB0WynSWPepgeDJ_ByuBE30YkrM3wQ";

// 1. RECEIVE showToast PROP
function CreateCampaign({ showToast }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [goal, setGoal] = useState("");
  const [imageFile, setImageFile] = useState(null); 
  const [imageUrl, setImageUrl] = useState("");     
  const [isLoading, setIsLoading] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const preview = URL.createObjectURL(file);
      setImageUrl(preview);
    }
  };

  const uploadToIPFS = async (file) => {
    if (!file) return;
    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
        headers: { 'Authorization': `Bearer ${PINATA_JWT}` }
      });
      return `https://gateway.pinata.cloud/ipfs/${res.data.IpfsHash}`;
    } catch (error) {
      console.error("Error uploading to Pinata:", error);
      throw new Error("Image upload failed");
    }
  };

  const createCampaign = async () => {
    // 2. USE showToast INSTEAD OF ALERT
    if (!title || !description || !goal || !imageUrl) {
      if(showToast) {
        showToast("Please fill in all fields!", "error");
      } else {
        alert("Please fill in all fields!"); // Fallback just in case
      }
      return;
    }

    try {
      setIsLoading(true);
      const contract = await getContract();
      if (!contract) return;

      const goalInWei = ethers.utils.parseEther(goal);
      
      let finalUrl = imageUrl;
      if (imageFile) {
        setUploadingImage(true);
        if(showToast) showToast("Uploading image... ‚è≥", "success");
        
        try {
          finalUrl = await uploadToIPFS(imageFile);
        } catch (uploadError) {
          if(showToast) showToast("Image upload failed.", "error");
          setIsLoading(false);
          setUploadingImage(false);
          return;
        }
        setUploadingImage(false);
      }

      if(showToast) showToast("Open Wallet to Confirm...", "success");

      const tx = await contract.createCampaign(
        title,
        description,
        goalInWei,
        finalUrl
      );

      await tx.wait();

      if(showToast) showToast("Campaign Created Successfully! üéâ", "success");
      
      setTimeout(() => {
        navigate("/");
      }, 2000);

    } catch (error) {
      console.error("Error creating campaign:", error);
      if(showToast) showToast("Transaction failed!", "error");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="create-container">
      <div className="create-form">
        <h2 className="form-title">Start a Campaign</h2>
        
        <div className="form-group">
          <label>Campaign Title</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} className="form-input" />
        </div>

        <div className="form-group">
          <label>Story</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} rows="4" className="form-textarea" />
        </div>

        <div className="form-group">
          <label>Goal Amount (ETH)</label>
          <div className="funding-input-container">
            <input type="number" value={goal} onChange={(e) => setGoal(e.target.value)} className="funding-input" />
            <span className="eth-badge">ETH</span>
          </div>
        </div>

        <div className="form-group">
          <label>Campaign Image</label>
          <div className="image-upload-wrapper">
            <input type="file" id="file-upload" className="hidden-file-input" accept="image/*" onChange={handleFileChange} />
            <label htmlFor="file-upload" className="file-upload-box">
              <span className="upload-icon">üìÅ</span>
              <span>Click to Upload from Device</span>
            </label>
            <div className="or-divider">OR</div>
            <input type="text" value={imageUrl} onChange={(e) => { setImageUrl(e.target.value); setImageFile(null); }} className="form-input" />
          </div>
          {imageUrl && (
            <div className="image-preview-container">
              <img src={imageUrl} alt="Preview" className="image-preview" />
              <button className="clear-img-btn" onClick={() => { setImageUrl(""); setImageFile(null); }}>‚úï</button>
            </div>
          )}
        </div>

        <button onClick={createCampaign} disabled={isLoading || uploadingImage} className="create-btn">
          {uploadingImage ? "Uploading Image..." : isLoading ? "Processing..." : "Launch Campaign"}
        </button>
      </div>
    </div>
  );
}

export default CreateCampaign;
