import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { connectWallet } from "../connectWallet";
import "../css/Navbar.css";

function Navbar({ walletAddress, setWalletAddress, theme, toggleTheme }) {
  const [menuOpen, setMenuOpen] = useState(false);

  const handleConnect = async () => {
    const signer = await connectWallet();
    if (signer) {
      const addr = await signer.getAddress();
      setWalletAddress(addr);
    }
  };

  useEffect(() => {
    if (!window.ethereum) return;
    window.ethereum.request({ method: "eth_accounts" }).then(accounts => {
      if (accounts.length) setWalletAddress(accounts[0]);
    });
    window.ethereum.on("accountsChanged", accounts => {
      setWalletAddress(accounts[0] || "");
    });
  }, [setWalletAddress]);

  const shortAddr = walletAddress
    ? `${walletAddress.slice(0, 6)}...${walletAddress.slice(-4)}`
    : "Connect Wallet";

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-logo">CrowdFund.</div>

        <div className={`navbar-links ${menuOpen ? "active" : ""}`}>
          <NavLink to="/" className="nav-link" onClick={() => setMenuOpen(false)}>All Campaigns</NavLink>
          <NavLink to="/create" className="nav-link" onClick={() => setMenuOpen(false)}>Start Campaign</NavLink>
          <NavLink to="/mycampaigns" className="nav-link" onClick={() => setMenuOpen(false)}>My Dashboard</NavLink>
          <button className="connect-btn mobile-only" onClick={handleConnect}>{shortAddr}</button>
        </div>

        <div className="navbar-actions">
          {/* THEME TOGGLE */}
          <button className="theme-btn" onClick={toggleTheme} aria-label="Toggle Theme">
            {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
          </button>

          <button className="connect-btn desktop-only" onClick={handleConnect}>{shortAddr}</button>
          <div className="hamburger" onClick={() => setMenuOpen(!menuOpen)}>‚ò∞</div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
