import { useEffect, useState } from "react";
import { getContract } from "../connectWallet";
import { ethers } from "ethers";
import { useNavigate } from "react-router-dom";
import "../css/AllCampaigns.css";

function AllCampaigns() {
  const [campaigns, setCampaigns] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const fetchCampaigns = async () => {
    setIsLoading(true);
    try {
      const contract = await getContract();
      if (!contract) return;
      const campaignsData = await contract.getAllCampaigns();
      
      const formattedCampaigns = campaignsData.map((campaign, index) => {
        const goal = ethers.utils.formatEther(campaign.goal.toString());
        const fundsRaised = ethers.utils.formatEther(campaign.fundsRaised.toString());
        const progressPercentage = Math.min(
          (parseFloat(fundsRaised) / parseFloat(goal)) * 100,
          100
        );
        const createdAt = new Date(campaign.createdAt.toNumber() * 1000).toLocaleDateString();
        
        return {
          id: index,
          owner: campaign.owner,
          title: campaign.title,
          description: campaign.description,
          goal,
          fundsRaised,
          completed: campaign.completed,
          imageUrl: campaign.imageUrl,
          progressPercentage,
          createdAt,
        };
      }).sort((a, b) => b.id - a.id); // Show newest first based on ID

      setCampaigns(formattedCampaigns);
    } catch (error) {
      console.error("Error fetching campaigns:", error);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    fetchCampaigns();
  }, []);

  return (
    <div className="campaigns-page">
      <div className="header-section">
        <h2 className="page-title">Explore Campaigns</h2>
        <p className="page-subtitle">Support innovative projects on the blockchain.</p>
      </div>

      <div className="campaigns-grid">
        {isLoading ? (
          <div className="loading-spinner">Loading Blockchain Data...</div>
        ) : (
          campaigns.map((campaign) => {
            const isClosed =
              campaign.completed ||
              parseFloat(campaign.fundsRaised) >= parseFloat(campaign.goal);

            return (
              <div
                key={campaign.id}
                className="campaign-card"
                onClick={() => navigate(`/campaign/${campaign.id}`)}
              >
                {/* Image Section */}
                <div className="card-image-wrapper">
                  <img src={campaign.imageUrl} alt={campaign.title} className="card-img" />
                  <span className={`status-badge ${isClosed ? "closed" : "open"}`}>
                    {isClosed ? "Completed" : "Active"}
                  </span>
                </div>

                {/* Content Section */}
                <div className="card-content">
                  <div className="card-header">
                    <h3 className="card-title">{campaign.title}</h3>
                    <p className="card-desc">
                      {campaign.description.length > 80 
                        ? campaign.description.substring(0, 80) + "..." 
                        : campaign.description}
                    </p>
                  </div>

                  {/* Progress Section */}
                  <div className="progress-section">
                    <div className="progress-labels">
                      <span className="raised-val">{campaign.fundsRaised} ETH</span>
                      <span className="goal-val">of {campaign.goal} ETH</span>
                    </div>
                    <div className="progress-track">
                      <div 
                        className="progress-fill" 
                        style={{ width: `${campaign.progressPercentage}%` }}
                      ></div>
                    </div>
                  </div>

                  {/* Footer / Creator Info */}
                  <div className="card-footer">
                    <div className="creator-info">
                      <div className="avatar-placeholder"></div>
                      <span className="creator-address">
                        By {campaign.owner.slice(0, 6)}...{campaign.owner.slice(-4)}
                      </span>
                    </div>
                    <span className="date-tag">{campaign.createdAt}</span>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>

      <button className="refresh-fab" onClick={fetchCampaigns} title="Refresh Data">
        â†»
      </button>
    </div>
  );
}

export default AllCampaigns;
