import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Menu, X, Wallet } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { connectWallet } from "../connectWallet";

// Responsive, accessible navbar using TailwindCSS, Framer Motion, and lucide-react icons.
// Props:
// - walletAddress (string | null)  - currently connected address
// - setWalletAddress (fn)          - setter to save the connected address

export default function ResponsiveNavbar({ walletAddress, setWalletAddress }) {
  const [open, setOpen] = useState(false);

  const handleConnect = async () => {
    try {
      const signer = await connectWallet();
      if (signer) {
        const addr = await signer.getAddress();
        // normalize to lowercase to match the rest of the app
        setWalletAddress(addr.toLowerCase());
      }
    } catch (err) {
      console.error("Wallet connection failed:", err);
    }
  };

  const shortAddr = (addr) => {
    if (!addr) return null;
    return `${addr.slice(0, 6)}...${addr.slice(-4)}`;
  };

  return (
    <header className="sticky top-0 z-40 bg-white/80 backdrop-blur-sm shadow-md">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Left: Brand / Logo */}
          <div className="flex items-center gap-3">
            <Link to="/" className="flex items-center gap-2 no-underline">
              <div className="w-10 h-10 rounded-md bg-gradient-to-br from-indigo-600 to-purple-500 flex items-center justify-center text-white font-bold">
                CF
              </div>
              <div className="hidden sm:block">
                <span className="text-lg font-semibold text-slate-800">CrowdFundX</span>
                <div className="text-xs text-slate-500">Deploy. Fund. Impact.</div>
              </div>
            </Link>
          </div>

          {/* Center: Nav links (desktop) */}
          <nav className="hidden md:flex space-x-6 items-center" aria-label="Primary">
            <Link to="/" className="text-slate-700 hover:text-slate-900 font-medium">
              All Campaigns
            </Link>
            <Link to="/create" className="text-slate-700 hover:text-slate-900 font-medium">
              Create Campaign
            </Link>
            <Link to="/mycampaigns" className="text-slate-700 hover:text-slate-900 font-medium">
              My Campaigns
            </Link>
          </nav>

          {/* Right: Connect button + mobile menu button */}
          <div className="flex items-center gap-3">
            <button
              onClick={handleConnect}
              className="inline-flex items-center gap-2 px-3 py-2 rounded-md border border-slate-200 bg-white hover:shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500"
              aria-label={walletAddress ? `Connected wallet ${walletAddress}` : "Connect wallet"}
            >
              <Wallet size={16} />
              <span className="text-sm font-medium text-slate-700">
                {walletAddress ? shortAddr(walletAddress) : "Connect Wallet"}
              </span>
            </button>

            {/* Mobile menu button */}
            <button
              onClick={() => setOpen((v) => !v)}
              className="md:hidden inline-flex items-center justify-center p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
              aria-label={open ? "Close menu" : "Open menu"}
              aria-expanded={open}
            >
              {open ? <X size={20} /> : <Menu size={20} />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile nav - animated */}
      <AnimatePresence>
        {open && (
          <motion.nav
            initial={{ opacity: 0, y: -8 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -8 }}
            transition={{ duration: 0.18 }}
            className="md:hidden border-t border-slate-100 bg-white/95"
          >
            <div className="px-4 pt-4 pb-6 space-y-3">
              <Link
                to="/"
                onClick={() => setOpen(false)}
                className="block text-slate-800 font-medium py-2"
              >
                All Campaigns
              </Link>
              <Link
                to="/create"
                onClick={() => setOpen(false)}
                className="block text-slate-800 font-medium py-2"
              >
                Create Campaign
              </Link>
              <Link
                to="/mycampaigns"
                onClick={() => setOpen(false)}
                className="block text-slate-800 font-medium py-2"
              >
                My Campaigns
              </Link>

              <div className="pt-2 border-t border-slate-100">
                <button
                  onClick={() => {
                    handleConnect();
                    setOpen(false);
                  }}
                  className="w-full inline-flex items-center justify-center gap-2 px-3 py-2 rounded-md border border-slate-200 bg-white hover:shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 mt-3"
                >
                  <Wallet size={16} />
                  <span className="text-sm font-medium text-slate-700">
                    {walletAddress ? shortAddr(walletAddress) : "Connect Wallet"}
                  </span>
                </button>
              </div>
            </div>
          </motion.nav>
        )}
      </AnimatePresence>
    </header>
  );
}
