import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import AllCampaigns from "./components/AllCampaigns";
import CreateCampaign from "./components/CreateCampaign";
import MyCampaigns from "./components/MyCampaigns";
import Dashboard from "./components/Dashboard";
import { useState, useEffect } from "react";

function App() {
  const [walletAddress, setWalletAddress] = useState("");
  const [toast, setToast] = useState({ show: false, msg: "", type: "success" });

  // --- 1. THEME STATE ---
  const [theme, setTheme] = useState("dark"); // Default to dark

  // --- 2. TOGGLE FUNCTION ---
  const toggleTheme = () => {
    const newTheme = theme === "dark" ? "light" : "dark";
    setTheme(newTheme);
    document.documentElement.setAttribute("data-theme", newTheme);
    localStorage.setItem("theme", newTheme); // Save preference
  };

  // --- 3. LOAD SAVED THEME ON START ---
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "dark";
    setTheme(savedTheme);
    document.documentElement.setAttribute("data-theme", savedTheme);
  }, []);

  const showToast = (message, type = "success") => {
    setToast({ show: true, msg: message, type: type });
    setTimeout(() => { setToast({ show: false, msg: "", type: "" }); }, 3500);
  };

  return (
    <Router>
      <div className="App">
        {/* Toast UI Code... (Keep your existing toast code here) */}
        
        {/* Pass toggleTheme and current theme to Navbar */}
        <Navbar 
          walletAddress={walletAddress} 
          setWalletAddress={setWalletAddress} 
          theme={theme}
          toggleTheme={toggleTheme}
        />
        
        <Routes>
          <Route path="/" element={<AllCampaigns />} />
          <Route path="/create" element={<CreateCampaign showToast={showToast} />} />
          <Route path="/mycampaigns" element={<MyCampaigns />} />
          <Route path="/campaign/:id" element={<Dashboard showToast={showToast} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
